import React, { useState } from 'react';

function App() {
  const [number, setNumber] = useState('');
  const [list, setList] = useState([]);

  // Thêm số vào danh sách
  const handleAddNumber = () => {
    if (number !== '' && !isNaN(number)) {
      setList([...list, Number(number)]);
      setNumber('');
    }
  };

  // Sắp xếp tăng dần
  const sortAsc = () => {
    const sorted = [...list].sort((a, b) => a - b);
    setList(sorted);
  };

  // Sắp xếp giảm dần
  const sortDesc = () => {
    const sorted = [...list].sort((a, b) => b - a);
    setList(sorted);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Form nhập số & Sắp xếp</h2>

      <input
        type="number"
        value={number}
        onChange={(e) => setNumber(e.target.value)}
        placeholder="Nhập số..."
      />
      <button onClick={handleAddNumber} style={{ marginLeft: '10px' }}>Thêm số</button>

      <div style={{ marginTop: '20px' }}>
        <button onClick={sortAsc}>Sắp xếp Tăng dần</button>
        <button onClick={sortDesc} style={{ marginLeft: '10px' }}>Sắp xếp Giảm dần</button>
      </div>

      <h3>Danh sách số:</h3>
      <ul>
        {list.map((num, index) => (
          <li key={index}>{num}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
