// src/pages/About.js
import React from 'react';
import bannerImage from ''; // Đường dẫn ảnh

function About() {
  return (
    <div>
      {/* Ảnh bìa */}
      <div style={{ position: 'relative', width: '100%', height: '300px', overflow: 'hidden' }}>
        <img
          src={bannerImage}
          alt="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTtzwh4-T4e_UDYZ0hbd_dpKTGBDCJCn3bnUg&s"
          style={{ width: '100%', height: '100%', objectFit: 'cover' }}
        />
        <div
          style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            color: 'white',
            fontSize: '36px',
            fontWeight: 'bold',
            textShadow: '2px 2px 4px rgba(0,0,0,0.7)',
          }}
        >
          Giới Thiệu
        </div>
      </div>

      {/* Nội dung chính */}
      <div style={{ padding: '30px', maxWidth: '900px', margin: '0 auto' }}>
        <h2>Về chúng tôi</h2>
        <p>
          Chúng tôi là một đội ngũ đam mê công nghệ, chuyên phát triển các ứng dụng web hiện đại bằng React.
          Mục tiêu của chúng tôi là giúp người dùng dễ dàng quản lý sản phẩm, dữ liệu, hình ảnh và trải nghiệm web mượt mà.
        </p>
        <p>
          Dự án này bao gồm các chức năng như thêm/sửa/xoá sản phẩm, tải hình ảnh, kết nối cơ sở dữ liệu và hiển thị danh sách sản phẩm.
        </p>
      </div>
    </div>
  );
}

export default About;
