import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FormPage.css';

function FormPage() {
  const [formData, setFormData] = useState({ name: '', email: '', image: '' });
  const [file, setFile] = useState(null); // ✅ Thêm state lưu file
  const [users, setUsers] = useState([]);
  const [editingId, setEditingId] = useState(null);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:3002/product/gets');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]); // ✅ Lấy file từ input
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData(); // ✅ Dùng FormData để gửi file
    data.append('name', formData.name);
    data.append('email', formData.email);
    if (file) data.append('image', file); // ✅ Thêm file nếu có

    if (editingId) {
      try {
        await axios.put(`http://localhost:3002/product/update/${editingId}`, data, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
        setEditingId(null);
        setFormData({ name: '', email: '', image: '' });
        setFile(null);
        fetchUsers();
      } catch (error) {
        console.error('Error updating user:', error);
      }
    } else {
      try {
        await axios.post('http://localhost:3002/product/add', data, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
        setFormData({ name: '', email: '', image: '' });
        setFile(null);
        fetchUsers();
      } catch (error) {
        console.error('Error adding user:', error);
      }
    }
  };

  const handleEdit = (user) => {
    setFormData({ name: user.name, email: user.email, image: user.image });
    setEditingId(user.id);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3002/product/delete/${id}`);
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  return (
    <div>
      <h2>{editingId ? 'Sửa sản phẩm' : 'Thêm sản phẩm'}</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <input
          type="text"
          name="name"
          placeholder="Tên sản phẩm"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="file"
          name="image"
          onChange={handleFileChange} // ✅ Lấy file
        />
        <button type="submit">{editingId ? 'Cập nhật' : 'Thêm sản phẩm'}</button>
        {editingId && (
          <button type="button" onClick={() => {
            setEditingId(null);
            setFormData({ name: '', email: '', image: '' });
            setFile(null);
          }}>Hủy sửa</button>
        )}
      </form>

      <h2>Danh sách sản phẩm</h2>
      <table border="1" cellPadding="10">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Image</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>
                {user.image ? (
                  <img src={user.image} alt={user.name} width="100" />
                ) : (
                  <img src="path/to/default-image.jpg" alt="Default" width="100" />
                )}
              </td>
              <td>
                <button onClick={() => handleEdit(user)}>Sửa</button>
                <button onClick={() => handleDelete(user.id)}>Xóa</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default FormPage;
