// src/components/Footer.js
function Footer() {
  return (
    <footer
      style={{
        padding: '10px',
        backgroundColor: '#222',
        color: '#fff',
        marginTop: '20px',
        textAlign: 'center', // Căn giữa nội dung
      }}
    >
      <p>Bản quyền © 2025 - Tên công ty của bạn</p>
    </footer>
  );
}

export default Footer;
