import React, { useState } from 'react';

function App() {
  const [numbers, setNumbers] = useState([8, 3, 1, 7, 4, 2, 6]);
  const [sortOrder, setSortOrder] = useState(null); // 'asc' | 'desc' | null

  const sortAscending = () => {
    const sorted = [...numbers].sort((a, b) => a - b);
    setNumbers(sorted);
    setSortOrder('asc');
  };

  const sortDescending = () => {
    const sorted = [...numbers].sort((a, b) => b - a);
    setNumbers(sorted);
    setSortOrder('desc');
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h2>React - Sắp xếp số</h2>
      <p>Mảng số: {numbers.join(', ')}</p>

      <button onClick={sortAscending} style={{ marginRight: '10px' }}>
        Sắp xếp tăng dần
      </button>

      <button onClick={sortDescending}>
        Sắp xếp giảm dần
      </button>

      {sortOrder && (
        <p style={{ marginTop: '20px' }}>
          Đã sắp xếp theo: <strong>{sortOrder === 'asc' ? 'Tăng dần' : 'Giảm dần'}</strong>
        </p>
      )}
    </div>
  );
}

export default App;
