import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FormPage.css'; // Tùy chỉnh CSS cho đẹp hơn

function FormPage() {
  const [formData, setFormData] = useState({ id: '', name: '', email: '', image: '', date: '' });
  const [products, setProducts] = useState([]);
  const [isEditing, setIsEditing] = useState(false);

  // Hàm thay đổi giá trị input
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Hàm thêm hoặc sửa sản phẩm
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isEditing) {
      // Sửa sản phẩm
      try {
        await axios.put(`http://localhost:3002/products/${formData.id}`, formData);
        setIsEditing(false);
        setFormData({ id: '', name: '', email: '', image: '', date: '' });
        fetchProducts(); // Cập nhật lại danh sách
      } catch (error) {
        console.error('Error editing product:', error);
      }
    } else {
      // Thêm sản phẩm
      try {
        await axios.post('http://localhost:3002/products', formData);
        setFormData({ id: '', name: '', email: '', image: '', date: '' });
        fetchProducts(); // Cập nhật lại danh sách
      } catch (error) {
        console.error('Error adding product:', error);
      }
    }
  };

  // Hàm xóa sản phẩm
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3002/products/${id}`);
      fetchProducts(); // Cập nhật lại danh sách
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  // Hàm sửa sản phẩm
  const handleEdit = (product) => {
    setFormData(product);
    setIsEditing(true);
  };

  // Lấy danh sách sản phẩm từ server
  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:3002/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  useEffect(() => {
    fetchProducts(); // Lấy dữ liệu khi component mount
  }, []);

  return (
    <div className="form-page">
      <h2>Thêm/Sửa Sản Phẩm</h2>
      <form onSubmit={handleSubmit} className="form">
        <input
          type="text"
          name="name"
          placeholder="Tên sản phẩm"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="image"
          placeholder="Link hình ảnh"
          value={formData.image}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
          required
        />
        <button type="submit">{isEditing ? 'Sửa sản phẩm' : 'Thêm sản phẩm'}</button>
      </form>

      <h2>Danh Sách Sản Phẩm</h2>
      <table border="1" cellPadding="10" style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Image</th>
            <th>Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.id}>
              <td>{product.id}</td>
              <td>{product.name}</td>
              <td>{product.email}</td>
              <td><img src={product.image} alt={product.name} style={{ width: '50px' }} /></td>
              <td>{product.date}</td>
              <td>
                <button onClick={() => handleEdit(product)}>Sửa</button>
                <button onClick={() => handleDelete(product.id)}>Xóa</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default FormPage;
