import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FormPage.css'; 

function FormPage() {
  const [formData, setFormData] = useState({ name: '', email: '', image: null });
  const [users, setUsers] = useState([]);
  const [editingId, setEditingId] = useState(null);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:3002/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleChange = (e) => {
    if (e.target.type === 'file') {
      setFormData({ ...formData, [e.target.name]: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formPayload = new FormData();
    formPayload.append('name', formData.name);
    formPayload.append('email', formData.email);
    formPayload.append('image', formData.image);

    if (editingId) {
      // Nếu đang sửa thì gọi PUT API
      try {
        await axios.put(`http://localhost:3002/update-user/${editingId}`, formPayload, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        setEditingId(null);
        setFormData({ name: '', email: '', image: null });
        fetchUsers();
      } catch (error) {
        console.error('Error updating user:', error);
      }
    } else {
      // Nếu không thì gọi POST API để thêm mới
      try {
        await axios.post('http://localhost:3002/add-user', formPayload, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        setFormData({ name: '', email: '', image: null });
        fetchUsers();
      } catch (error) {
        console.error('Error adding user:', error);
      }
    }
  };

  const handleEdit = (user) => {
    setFormData({ name: user.name, email: user.email, image: user.image });
    setEditingId(user.id);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3002/delete-user/${id}`);
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  return (
    <div>
      <h2>{editingId ? 'Sửa sản phẩm' : 'Thêm sản phẩm'}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Tên sản phẩm"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="file"
          name="image"
          onChange={handleChange}
        />
        <button type="submit">{editingId ? 'Cập nhật' : 'Thêm sản phẩm'}</button>
        {editingId && (
          <button type="button" onClick={() => {
            setEditingId(null);
            setFormData({ name: '', email: '', image: null });
          }}>Hủy sửa</button>
        )}
      </form>

      <h2>Danh sách sản phẩm</h2>
      <table border="1" cellPadding="10" style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Image</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td><img src={user.image} alt={user.name} width="100" /></td>
              <td>
                <button onClick={() => handleEdit(user)}>Sửa</button>
                <button onClick={() => handleDelete(user.id)}>Xóa</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default FormPage;
