import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FormPage.css';

function FormPage() {
  const [formData, setFormData] = useState({ id: '', name: '', email: '', image: '', date: '' });
  const [users, setUsers] = useState([]);
  const [isEditing, setIsEditing] = useState(false); // Dùng để kiểm tra xem có đang chỉnh sửa không

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEditing) {
        // Cập nhật người dùng
        await axios.put(`http://localhost:3002/update-user/${formData.id}`, formData);
        setIsEditing(false); // Đổi về chế độ không chỉnh sửa
      } else {
        // Thêm người dùng
        await axios.post('http://localhost:3002/add-user', formData);
      }
      setFormData({ name: '', email: '', image: '', date: '' }); // Reset form
      fetchUsers(); // Lấy lại danh sách người dùng
    } catch (error) {
      console.error('Error handling form submission:', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const res = await axios.get('http://localhost:3002/users');
      setUsers(res.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const handleEdit = (user) => {
    setFormData({
      id: user.id,
      name: user.name,
      email: user.email,
      image: user.image,
      date: user.date,
    });
    setIsEditing(true); // Đổi sang chế độ chỉnh sửa
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3002/delete-user/${id}`);
      fetchUsers(); // Cập nhật lại danh sách sau khi xóa
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <div>
      <h2 className="heading">{isEditing ? 'Sửa Sản Phẩm' : 'Thêm Sản Phẩm'}</h2>
      <form onSubmit={handleSubmit} className="form">
        <input
          type="text"
          name="name"
          placeholder="Tên sản phẩm"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="image"
          placeholder="Link hình ảnh"
          value={formData.image}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          name="date"
          placeholder="Ngày"
          value={formData.date}
          onChange={handleChange}
          required
        />
        <button type="submit">{isEditing ? 'Cập nhật sản phẩm' : 'Thêm sản phẩm'}</button>
      </form>

      <h2 className="heading">Danh sách sản phẩm</h2>
      <table border="1" cellPadding="10" style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Image</th>
            <th>Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td><img src={user.image} alt={user.name} width="50" /></td>
              <td>{user.date}</td>
              <td>
                <button onClick={() => handleEdit(user)}>Sửa</button>
                <button onClick={() => handleDelete(user.id)}>Xóa</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default FormPage;
