import React, { useState, useEffect } from 'react';
import axios from 'axios';

function FormPage() {
  const [formData, setFormData] = useState({ name: '', email: '', image: '' });
  const [users, setUsers] = useState([]);

  // Hàm lấy danh sách người dùng từ backend
  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:3002/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  useEffect(() => {
    fetchUsers();  // Gọi API để lấy danh sách người dùng khi component mount
  }, []);

  // Hàm xử lý thay đổi dữ liệu trong form
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Hàm xử lý gửi form
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3002/add-user', formData);
      setFormData({ name: '', email: '', image: '' }); // Reset form
      fetchUsers(); // Load lại danh sách người dùng
    } catch (error) {
      console.error('Error adding user:', error);
    }
  };

  // Hàm xóa người dùng
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3002/delete-user/${id}`);
      fetchUsers(); // Load lại danh sách người dùng
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  return (
    <div>
      <h2>Form Thêm Sản Phẩm</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Tên sản phẩm"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="image"
          placeholder="Link hình ảnh"
          value={formData.image}
          onChange={handleChange}
          required
        />
        <button type="submit">Thêm sản phẩm</button>
      </form>

      <h2>Danh sách sản phẩm</h2>
      <table border="1" cellPadding="10" style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Image</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td><img src={user.image} alt={user.name} width="100" /></td>
              <td>
                <button onClick={() => handleDelete(user.id)}>Xóa</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default FormPage;
