import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ProductForm from './ProductForm';

function Dashboard() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    axios.get('http://localhost:3002/add-user')
      .then(response => {
        setProducts(response.data);
      })
      .catch(error => {
        console.error('Lỗi khi lấy dữ liệu:', error);
      });
  };

  const handleAddProduct = (newProduct) => {
    setProducts([newProduct, ...products]);
  };

  return (
    <div>
      <h1>Dashboard Quản Lý Đồng Hồ</h1>
      <ProductForm onAdd={handleAddProduct} />

      <table border="1" cellPadding="10" cellSpacing="0" style={{ width: '100%', marginTop: '20px', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Tên</th>
            <th>Email</th>
            <th>Hình ảnh</th>
            <th>Ngày</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product.id}>
              <td>{product.id}</td>
              <td>{product.name}</td>
              <td>{product.email}</td>
              <td>
                <img src={product.image} alt={product.name} style={{ width: '100px', height: '80px', objectFit: 'cover' }} />
              </td>
              <td>{product.date}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Dashboard;
