import React, { useState } from 'react';

function App() {
  const [numbers, setNumbers] = useState([8, 3, 1, 7, 4, 2, 6]);
  const [sortOrder, setSortOrder] = useState(null);
  const [inputs, setInputs] = useState({ num1: '', num2: '', num3: '' });

  const sortAscending = () => {
    const sorted = [...numbers].sort((a, b) => a - b);
    setNumbers(sorted);
    setSortOrder('asc');
  };

  const sortDescending = () => {
    const sorted = [...numbers].sort((a, b) => b - a);
    setNumbers(sorted);
    setSortOrder('desc');
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInputs((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newNumbers = [inputs.num1, inputs.num2, inputs.num3].map(Number);
    if (newNumbers.some(isNaN)) {
      alert('Vui lòng nhập 3 số hợp lệ');
      return;
    }
    setNumbers(newNumbers);
    setSortOrder(null); // Reset trạng thái sắp xếp
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h2>React - Sắp xếp số</h2>

      <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          name="num1"
          value={inputs.num1}
          onChange={handleChange}
          placeholder="Số 1"
          style={{ marginRight: '10px' }}
        />
        <input
          type="text"
          name="num2"
          value={inputs.num2}
          onChange={handleChange}
          placeholder="Số 2"
          style={{ marginRight: '10px' }}
        />
        <input
          type="text"
          name="num3"
          value={inputs.num3}
          onChange={handleChange}
          placeholder="Số 3"
          style={{ marginRight: '10px' }}
        />
        <button type="submit">Nhập 3 số</button>
      </form>

      <p><strong>Mảng số:</strong> {numbers.join(', ')}</p>

      <button onClick={sortAscending} style={{ marginRight: '10px' }}>
        Sắp xếp tăng dần
      </button>

      <button onClick={sortDescending}>
        Sắp xếp giảm dần
      </button>

      {sortOrder && (
        <p style={{ marginTop: '20px' }}>
          Đã sắp xếp theo: <strong>{sortOrder === 'asc' ? 'Tăng dần' : 'Giảm dần'}</strong>
        </p>
      )}
    </div>
  );
}

export default App;
