import React, { useState } from 'react';
import axios from 'axios';

function ProductForm({ onAdd }) {
  const [formData, setFormData] = useState({
    id: '',
    name: '',
    email: '',
    image: '',
    date: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:3002/add-user', formData)
      .then(response => {
        console.log('Thêm thành công:', response.data);
        onAdd(response.data); // Thêm vào danh sách
        setFormData({ id: '', name: '', email: '', image: '', date: '' });
      })
      .catch(error => {
        console.error('Lỗi khi thêm user:', error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="id" value={formData.id} onChange={handleChange} placeholder="ID" required />
      <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Name" required />
      <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Email" required />
      <input type="text" name="image" value={formData.image} onChange={handleChange} placeholder="Image URL" required />
      <input type="date" name="date" value={formData.date} onChange={handleChange} required />
      <button type="submit">Thêm</button>
    </form>
  );
}

export default ProductForm;
