import React, { useState, useEffect } from 'react';
import axios from 'axios';

function FormPage() {
  const [formData, setFormData] = useState({ name: '', email: '', image: '', date: '' });
  const [products, setProducts] = useState([]);
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const response = await axios.get('http://localhost:3002/product/gets');
    setProducts(response.data);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, image: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', formData.name);
    data.append('email', formData.email);
    data.append('date', formData.date);
    data.append('image', formData.image);

    if (editingId) {
      await axios.put(`http://localhost:3002/product/update/${editingId}`, data, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setEditingId(null);
    } else {
      await axios.post('http://localhost:3002/product/add', data, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
    }

    setFormData({ name: '', email: '', image: '', date: '' });
    fetchProducts();
  };

  const handleEdit = (product) => {
    setFormData({ name: product.name, email: product.email, image: '', date: product.date });
    setEditingId(product.id);
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:3002/product/delete/${id}`);
    fetchProducts();
  };

  return (
    <div>
      <h2>{editingId ? 'Edit Product' : 'Add Product'}</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleInputChange} required />
        <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleInputChange} required />
        <input type="date" name="date" value={formData.date} onChange={handleInputChange} required />
        <input type="file" name="image" onChange={handleFileChange} required={!editingId} />
        <button type="submit">{editingId ? 'Update' : 'Add'}</button>
      </form>

      <h2>Product List</h2>
      <table border="1" cellPadding="10">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Image</th>
            <th>Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.id}>
              <td>{p.id}</td>
              <td>{p.name}</td>
              <td>{p.email}</td>
              <td><img src={`http://localhost:3002/${p.image}`} alt={p.name} width="100" /></td>
              <td>{p.date}</td>
              <td>
                <button onClick={() => handleEdit(p)}>Edit</button>
                <button onClick={() => handleDelete(p.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default FormPage;
